mapper.select:
select 

from pump_full_coefficient
where collect_time = #{collectTime,jdbcType=TIMESTAMP}
  and well_code = #{wellCode,jdbcType=VARCHAR}

mapper.delete:
delete from pump_full_coefficient
where collect_time = #{collectTime,jdbcType=TIMESTAMP}
  and well_code = #{wellCode,jdbcType=VARCHAR}

mapper.update:
update pump_full_coefficient


    full_coefficient = #{fullCoefficient,jdbcType=NUMERIC},


    threshold = #{threshold,jdbcType=NUMERIC},


where collect_time = #{collectTime,jdbcType=TIMESTAMP}
  and well_code = #{wellCode,jdbcType=VARCHAR}

update pump_full_coefficient
set full_coefficient = #{fullCoefficient,jdbcType=NUMERIC},
  threshold = #{threshold,jdbcType=NUMERIC}
where collect_time = #{collectTime,jdbcType=TIMESTAMP}
  and well_code = #{wellCode,jdbcType=VARCHAR}
