mapper.select:
select 

from record_conditions
where well_type = #{wellType,jdbcType=VARCHAR}
  and cond_code = #{condCode,jdbcType=VARCHAR}

mapper.delete:
delete from record_conditions
where well_type = #{wellType,jdbcType=VARCHAR}
  and cond_code = #{condCode,jdbcType=VARCHAR}

mapper.insert:
insert into record_conditions (well_type, cond_code, cond_name
  )
values (#{wellType,jdbcType=VARCHAR}, #{condCode,jdbcType=VARCHAR}, #{condName,jdbcType=VARCHAR}
  )

mapper.update:
update record_conditions


    cond_name = #{condName,jdbcType=VARCHAR},


where well_type = #{wellType,jdbcType=VARCHAR}
  and cond_code = #{condCode,jdbcType=VARCHAR}

update record_conditions
set cond_name = #{condName,jdbcType=VARCHAR}
where well_type = #{wellType,jdbcType=VARCHAR}
  and cond_code = #{condCode,jdbcType=VARCHAR}
